import ScriptTag from 'react-script-tag';
import './App.css';
import { useStickyState } from "./hooks/useStickyState";


export default function App() {
  const [state, setState] = useStickyState(1, "sketchstate");

  function displayFrame() {
    if (state === 1){
      return <ScriptTag type="text/javascript" src="sketch.js" />
    } else{
      return <div>HELLLLLO</div>
    }
  }

  return (
    <div className="App">
      {displayFrame()}

      <button onClick={() => {
        return setState(2);
      }} >This is a button {state} </button>
    </div>
  );
}
